    private void BtActualizaConstraseñaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtActualizaConstraseñaActionPerformed
        ServicesUsuario servUsuario = new ServicesUsuario();
        if(TextActualizaNuevaContra.getText().equals(TextActualizaConfirmContra.getText())){
            servUsuario.actualizar_password(TextActualizaNuevaContra.getText(), ServicesUsuario.getUsuario());
            javax.swing.JOptionPane.showMessageDialog(this, "Los datos se guardaron correctamente. ");
        }
        else{
            javax.swing.JOptionPane.showMessageDialog(this, "Las contraseñas no coinciden");
        }
    }//GEN-LAST:event_BtActualizaConstraseñaActionPerformed

