    private void BtActualizaUsuarioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtActualizaUsuarioActionPerformed
        ServicesUsuario servUsuario = new ServicesUsuario();
        if(TextActualizaUsuario1.getText().equals(TextActualizaUsuario2.getText())){
            servUsuario.actualizar_usuario(TextActualizaUsuario.getText(), ServicesUsuario.getUsuario());
            javax.swing.JOptionPane.showMessageDialog(this, "Los datos se guardaron correctamente. ");
        }
        else{
            javax.swing.JOptionPane.showMessageDialog(this, "Las contraseñas no coinciden");
        }
    }//GEN-LAST:event_BtActualizaUsuarioActionPerformed

    private void BtActualizPerfilActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtActualizPerfilActionPerformed
        ServicesUsuario servUsuario = new ServicesUsuario();
        if(TextContraActual3.getText().equals(TextConfirmContrActual.getText())){
            servUsuario.actualizar_nombres(TextNuevoNombres.getText(), ServicesUsuario.getUsuario());
            servUsuario.actualizar_email(TextNuevoCorreoo.getText(), ServicesUsuario.getUsuario());
            javax.swing.JOptionPane.showMessageDialog(this, "Los datos se guardaron correctamente. ");
        }
        else{
            javax.swing.JOptionPane.showMessageDialog(this, "Las contraseñas no coinciden");
        }
    }//GEN-LAST:event_BtActualizPerfilActionPerformed

